package datatype;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   
		System.out.println("demo for loops");
		
		int i;
		for(i=1;i<=10;i++)
		{
		System.out.println(i);	
		}
		System.out.println("demo for while loops");
		int j=1;
		while(j<=10){
			System.out.println(j);
			j++;
		}
			System.out.println("demo for  do while loops");
			int k=1;
			do {
			System.out.println(k);
			k++;
			}
			while(k<=10);
		
			
			
		}
	}


