package datatype;

public class Operaters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// operators demo
		
		int i=10,j=20,k;
		k=i+j;
		System.out.println("addition of i and j"+k);
		k=i-j;
		System.out.println(" subtraction of of i and j"+k);
		k=i*j;
		System.out.println("multipication of i and j"+k);
		k=i/j;
		System.out.println("division of i and j"+k);
		k=i%j;
		System.out.println("modular of i and j"+k);
		//area of circle 
		double d ,r=20;
		d=Math.PI*r*r;
		System.out.println("area of circle "+d);
		
		
	}

}
