package datatype;

public class myfirstclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("all data types demo ");
		int i=10;
		System.out.println("value of i"+i);
		byte b=30;
		System.out.println("value of byte b"+b);
		short s=80;
		System.out.println("value of short s"+s);
		long l=60000;
		System.out.println("value of long l"+l);
		float f=45.45f;
		System.out.println("value of float f"+f);
		double d=65.6789;
		System.out.println("value of double d"+d);
		boolean t=false;
		System.out.println("value of booleab t"+t);
		char ch ='k';
		System.out.println("value of char ch"+ch);
		
	}

}
